export default {
  props: ['idaula', 'novo'],
  data: function data() {
    return {
      aula: Object,
      materialDidatico: '',
      valid: false,
      nome: '',
      categoria: '',
      qtSemana: '',
      nomeRules: [function (v) {
        return !!v || 'Preenchimento necessário';
      }],
      categoriaItems: ['Graduação', 'Pós-Graduação'],
      qtSemanaRules: [function (v) {
        return !!v || 'Preenchimento necessário';
      }]
    };
  },
  created: function created() {
    var _this = this;

    if (!this.novo) {
      this.$http.secured.get("/api/v1/aulas/".concat(this.idaula)).then(function (response) {
        _this.aula = response.data.data;
        _this.nome = _this.aula.nomeAula;
        _this.categoria = _this.aula.categoria;
        _this.qtSemana = _this.aula.quantidade;
        _this.materialDidatico = _this.aula.materialDidatico;
      }).catch(function (error) {
        return _this.setError(error, 'Nao consegue achar aula');
      });
    }
  },
  methods: {
    salvar: function salvar() {
      //let formData = new FormData()
      this.$emit('salvar');

      if (this.novo) {
        this.$http.secured.post('/api/v1/aulas/', {
          nomeAula: this.nome,
          quantidade: this.qtSemana,
          categoria: this.categoria,
          materialDidatico: this.materialDidatico
        });
      } else {
        this.$http.secured.patch("/api/v1/aulas/".concat(this.aula.id), {
          nomeAula: this.nome,
          quantidade: this.qtSemana,
          categoria: this.categoria,
          materialDidatico: this.materialDidatico
        });
      }
    },
    uploadFile: function uploadFile() {
      this.materialDidatico = this.$refs.inputFile.files[0];
    },
    fecharDialogo: function fecharDialogo() {
      this.$emit('fechar');
    }
  }
};
