export default {
  data: function data() {
    return {
      showPassword: false,
      rules: {
        required: function required(value) {
          return !!value || 'Campo obrigatório.';
        },
        email: function email(value) {
          var pattern = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
          return pattern.test(value) || 'E-mail inválido.';
        }
      },
      id: String,
      password: '',
      email: String,
      nomeCompleto: String,
      grupoPesquisa: String,
      sala: String,
      telefone: String,
      formacao: String,
      professor: Object
    };
  },
  created: function created() {
    var _this = this;

    this.$http.secured.get('/api/v1/professors').then(function (response) {
      _this.professor = response.data.data[0];
      _this.id = _this.professor.id;
      _this.email = _this.professor.email;
      _this.nomeCompleto = _this.professor.nomeProfessor;
      _this.sala = _this.professor.sala;
      _this.telefone = _this.professor.telefone;
      _this.formacao = _this.professor.formacao;
      _this.grupoPesquisa = _this.professor.grupoPesquisa;
    }).catch(function (error) {
      return _this.setError(error, 'Algo deu errado!');
    });
  },
  methods: {
    fechaDialogo: function fechaDialogo() {
      this.$emit('fechar');
    },
    salvarPerfil: function salvarPerfil() {
      this.$emit('salvar'), this.$http.secured.patch("/api/v1/professors/".concat(this.id), {
        nomeProfessor: this.nomeCompleto,
        email: this.email,
        sala: this.sala,
        telefone: this.telefone,
        formacao: this.formacao,
        password: this.password,
        grupoPesquisa: this.grupoPesquisa
      });
    },
    setError: function setError(error, text) {
      this.error = error.response && error.response.data && error.response.data.error || text;
    }
  }
};
