export default {
  props: ['idaluno', 'novo'],
  data: function data() {
    return {
      aluno: Object,
      valid: false,
      nomeCompleto: '',
      matricula: '',
      categoria: '',
      curso: '',
      nomeCompletoRules: [function (v) {
        return !!v || 'Preenchimento necessário';
      }],
      matriculaRules: [function (v) {
        return !!v || 'Preenchimento necessário';
      }],
      cursoRules: [function (v) {
        return !!v || 'Preenchimento necessário';
      }],
      categorias: ['Iniciação Científica', 'Mestrado', 'Doutorado', 'Pós-doutorado']
    };
  },
  created: function created() {
    var _this = this;

    if (!this.novo) {
      this.$http.secured.get("/api/v1/alunos/".concat(this.idaluno)).then(function (response) {
        _this.aluno = response.data.data;
        _this.nomeCompleto = _this.aluno.nome;
        _this.matricula = _this.aluno.matricula;
        _this.curso = _this.aluno.curso;
        _this.categoria = _this.aluno.categoria;
      }).catch(function (error) {
        return _this.setError(error, 'Nao consegue achar aluno');
      });
    }
  },
  methods: {
    fechaDialogo: function fechaDialogo() {
      this.$emit('fechar');
    },
    salvarAluno: function salvarAluno() {
      this.$emit('salvar');

      if (this.novo) {
        this.$http.secured.post('/api/v1/alunos/', {
          nome: this.nomeCompleto,
          matricula: this.matricula,
          curso: this.curso,
          categoria: this.categoria
        });
      } else {
        this.$http.secured.patch("/api/v1/alunos/".concat(this.aluno.id), {
          nome: this.nomeCompleto,
          matricula: this.matricula,
          curso: this.curso,
          categoria: this.categoria
        });
      }
    }
  }
};
