export default {
    props: ['aluno'],
    data: () => ({
        valid: false,
        nomeCompleto: '',
        matricula: '',
        categoria:'',
        curso: '',
        nomeCompletoRules: [
            v => !!v || 'Preenchimento necessário'
        ],
        matriculaRules: [
            v => !!v || 'Preenchimento necessário'
        ],
        cursoRules: [
            v => !!v || 'Preenchimento necessário'
        ],
        categorias: ['Graduação', 'Pós-graduação']
    }),
    created() {
        if(this.aluno) {
            this.nomeCompleto = this.aluno.nome
            this.matricula = this.aluno.matricula
            this.curso = this.aluno.curso
            this.categoria = this.aluno.categoria
        }
    },
    methods: {
        fechaDialogo() {
            this.$emit('fechar')
        },
        salvarAluno() {
            this.$emit('salvar')
        }
    }
}
